def calculate_pass_sequences(n, m, s, strengths):
    MOD = 10 ** 9 + 7
    s += 1
    dp = [[0] * n for _ in range(m + 1)]
    dp[0][s] = 1
    elems = {s}
    for i in range(1, m + 1):
        elems2 = set()
        for elem in elems:
            a = elem - strengths[i - 1]
            b = elem + strengths[i - 1]
            if a >= 0:
                dp[i][a] = (dp[i][a] + dp[i - 1][elem]) % MOD
                elems2.add(a)
            if b < n:
                dp[i][b] = (dp[i][b] + dp[i - 1][elem]) % MOD
                elems2.add(b)
        elems = elems2
    return dp[m]

