def minimum_shots_to_defeat_stormtroopers(n, x0, y0, troopers):
    slopes = []
    for (xi, yi) in troopers:
        if xi == x0:
            slopes.append((yi - y0) / (xi - x0))
        else:
            slopes.append('INF')
    unique_slopes = set(slopes)
    return len(unique_slopes)

