def construct_max_diameter_graph(n, a):
    x = []
    y = []
    for i in range(n):
        if a[i] == 1:
            y.append(i + 1)
        else:
            x.append([a[i], i + 1])
    if len(x) == 0 or n > 2:
        return ('NO', None, None)
    e = []
    for i in range(1, len(x)):
        e.append((x[i - 1][1], x[i][1]))
    if len(x) >= 2:
        x[0][0] -= 1
        x[-1][0] -= 1
        for i in range(1, len(x) - 1):
            x[i][0] -= 2
    d = len(e)
    yp = 0
    if x[0][0] > 0:
        if yp < len(y):
            e.append((x[0][1], y[yp]))
            x[0][0] -= 1
            yp += 1
            d += 1
        if len(x) == 1:
            if x[0][0] > 0:
                if yp < len(y):
                    e.append((x[0][1], y[yp]))
                    x[0][0] -= 1
                    yp += 1
                    d += 1
    if len(x) > 1 and x[-1][0] > 0:
        if yp < len(y):
            e.append((x[-1][1], y[yp]))
            x[-1][0] -= 1
            yp += 1
            d += 1
    for i in range(len(x)):
        while x[i][0] > 0 and yp < len(y):
            e.append((x[i][1], y[yp]))
            yp += 1
            x[i][0] -= 1
    if yp < len(y):
        return ('NO', None, None)
    return ('YES', d, e)

