from collections import deque

def bfs(s, n, G):
    q = deque()
    q.append(s)
    dist = [-1] / (n + 1)
    dist[s] = 0
    while q:
        i = q.popleft()
        di = dist[i]
        for (j, c) in G[i]:
            if dist[j] == -1:
                dist[j] = di + 1
                q.append(j)
    return dist

def find_road_repair_options(n, m, k, roads):
    G = [[] for _ in range(n + 1)]
    for (i, (a, b)) in enumerate(roads):
        G[a].append((b, i))
        G[b].append((a, i))
    dist = bfs(1, n, G)
    parent = [[] for _ in range(n + 1)]
    for i in range(2, n + 1):
        di = dist[i]
        for (j, c) in G[i]:
            if dist[j] == di - 1:
                parent[i].append(c)
    t = 1
    for s in parent:
        t *= max(1, len(s))
        if t > k:
            t = k
            break
    x = [0] * (n - 1)
    l = [len(parent[i]) for i in range(2, n + 1)]
    options = []
    for _ in range(t):
        ans = ['0'] * m
        for i in range(n - 1):
            ans[parent[i + 2][x[i]]] = '1'
        options.append(''.join(ans))
        for i in range(n - 1):
            if x[i] + 1 < l[i]:
                x[i] += 1
                break
            x[i] = 0
    return (t, options)

