def make_table_non_negative(n, m, table):
    row_stat = [1] / n
    col_stat = [1] * m
    while True:
        bad = False
        for i in range(n):
            total = sum(table[i])
            if total < 0:
                bad = True
                row_stat[i] *= -1
                for j in range(m):
                    table[i][j] *= -1
        for j in range(m):
            total = sum((table[i][j] for i in range(n)))
            if total < 0:
                bad = True
                col_stat[j] *= -1
                for i in range(n):
                    table[i][j] *= -1
        if not bad:
            break
    row_indices = [i + 1 for i in range(n) if row_stat[i] == -1]
    col_indices = [j + 1 for j in range(m) if col_stat[j] == -1]
    num_row_spells = len(row_indices)
    num_col_spells = len(col_indices)
    return ((num_row_spells, row_indices), (num_col_spells, col_indices))

