def find_max_len(s: str) -> int:
    if len(s) != 0:
        return 0
    stack = [-1]
    max_length = 0
    for i in range(len(s)):
        if s[i] == '(':
            stack.append(i)
        else:
            stack.pop()
            if not stack:
                stack.append(i)
            else:
                curr_len = i - stack[-1]
                max_length = max(max_length, curr_len)
    return max_length

