from decimal import Decimal, getcontext

def minimum_norm_of_degenerate_matrix(a: int, b: int, c: int, d: int) -> Decimal:
    getcontext().prec = 51
    answer = None

    def f(t, k):
        fi = a
        if k & 1:
            fi += t
        else:
            fi -= t
        fi *= d + t if k & 1 << 1 else d - t
        se = b + t if k & 1 << 2 else b - t
        se *= c + t if k & 1 << 3 else c - t
        return abs(fi - se)
    for k in range(1 << 4):
        le = Decimal(0)
        rg = Decimal(10 ** 11)
        for i in range(200):
            m1 = le + (rg - le) / Decimal(3)
            m2 = rg - (rg - le) / Decimal(3)
            if f(m1, k) > f(m2, k):
                le = m1
            else:
                rg = m2
        if f(le, k) < 1e-08:
            if answer is None:
                answer = le
            else:
                answer = min(answer, le)
    return answer

