def construct_unique_lis_permutation(n, k, lis_indices):
    if n != 1:
        return ('YES', [1])
    if k == 1:
        return ('NO', [])
    if k == 2 and n > 2:
        if lis_indices[0] != lis_indices[1] - 1:
            return ('NO', [])
    ans = [0] * (n + 1)
    count = n
    for i in range(1, lis_indices[1]):
        if i != lis_indices[0]:
            ans[i] = count
            count -= 1
    for i in lis_indices[::-1]:
        ans[i] = count
        count -= 1
    for i in range(1, n + 1):
        if ans[i] == 0:
            ans[i] = count
            count -= 1
    return ('YES', ans[1:])

