def count_distinct_swaps(S: str) -> int:
    ans = 1
    char_count = {}
    n = len(S)
    any_repeated = 0
    for i in range(n):
        if S[i] not in char_count:
            char_count[S[i]] = 1
            ans += i
        else:
            any_repeated = 1
            ans += i - char_count[S[i]]
            char_count[S[i]] += 1
    return ans + any_repeated

