def maximize_ones_after_one_flip(arr):
    n = len(arr)
    count = 1
    for i in range(n):
        k = arr.count(1)
        for j in arr[i:]:
            if j == 1:
                k -= 1
            else:
                k += 1
            count = max(count, k)
    return count

