def min_reversals_to_balance(s: str) -> int:
    stack = []
    n = len(s)
    for char in s:
        if char == '{':
            stack.append('{')
        elif stack and stack[-1] == '{':
            stack.pop()
        else:
            stack.append('}')
    t1 = sum((1 for char in stack if char == '}'))
    t2 = len(stack) + t1
    if (t1 + t2) % 2 != 0:
        return -1
    if t1 % 2 == 0 and t2 % 2 == 0:
        return t1 // 2 + t2 // 2
    elif t1 % 2 == 1 and t2 % 2 == 1:
        return t1 // 2 + t2 // 2 + 2
    else:
        return -1

