def find_smallest_display_number(n, display):
    from itertools import chain
    ls = list(map(int, display))
    a = ls[-1]
    stop = 1
    for (stop, b) in enumerate(ls):
        if b != a:
            break
    else:
        return '0' * n
    ls = ls[stop:] + ls[:stop]
    a = ls[0]
    l = []
    start = ma = tail = 0
    for (stop, b) in enumerate(ls):
        if b != a:
            le = stop - start
            if ma < le:
                (ma, tail) = (le, (b - a) % 10)
                l.clear()
                l.append(start)
            elif ma == le:
                tl = (b - a) % 10
                if tail > tl:
                    tail = tl
                    l.clear()
                    l.append(start)
                elif tail == tl:
                    l.append(start)
            (a, start) = (b, stop)
    le = n - start
    if ma < le:
        l.clear()
        l.append(start)
    elif ma == le:
        tl = (ls[0] - a) % 10
        if tail > tl:
            l.clear()
            l.append(start)
        elif tail == tl:
            l.append(start)
    for (i, start) in enumerate(l):
        base = ls[start]
        l[i] = ''.join((chr((a - base) % 10 + 48) for a in chain(ls[start:], ls[:start])))
    return min(l)

