def largest_rectangle_area(heights: list[int]) -> int:
    """"""
    stk = []
    ans = 0
    N = len(heights)
    for i in range(N):
        if not stk:
            stk.append((heights[i], i))
        elif stk[-1][0] < heights[i]:
            stk.append((heights[i], i))
        elif stk[-1][0] == heights[i]:
            pass
        else:
            lastpos = None
            while stk and stk[-1][0] > heights[i]:
                (nh, np) = stk[-1]
                lastpos = np
                stk.pop()
                ans = max(ans, nh * (i - np))
            stk.append((heights[i], lastpos if lastpos is not None else i))
    while stk:
        (nh, np) = stk[-1]
        stk.pop()
        ans = max(ans, nh * (N - np))
    return ans
