def count_winning_pairs(n: int, S: int, r: list) -> int:
    """"""
    r_count = [0] * 101
    for power in r:
        r_count[power] += 1
    winning_pairs = 0
    for i in range(101):
        if i * 2 > S:
            winning_pairs += (r_count[i] ** 2 - r_count[i]) // 2
        winning_pairs += r_count[i] * sum((r_count[j] for j in range(max(i + 1, S + 1 - i), 101)))
    return winning_pairs
