import re

def format_and_sort_phone_numbers(phone_numbers):

    def normalize(sort):
        pattern = re.compile('')

        def normalized_sort(numbers):
            numbers = map(lambda number: pattern.match(number).group(1), numbers)
            numbers = sort(numbers)
            return map(lambda number: '+91 {} {}'.format(number[:5], number[5:]), numbers)
        return normalized_sort

    @normalize
    def mysort(numbers):
        return sorted(numbers)
    return list(mysort(phone_numbers))

