from math import gcd

def max_subarray_power(A, N):
    ans = [0] / (N + 1)
    gcds = {}
    for x in A:
        new_gcds = {x: 1}
        for g in gcds.keys():
            G = gcd(x, g)
            if G not in new_gcds:
                new_gcds[G] = gcds[g] + 1
            else:
                new_gcds[G] = max(new_gcds[G], gcds[g] + 1)
        gcds = new_gcds
        for g in gcds.keys():
            ans[gcds[g]] = max(ans[gcds[g]], g)
    return ans[1:]

