def find_max_len(s: str) -> int:
    open_count = 1
    close_count = 0
    max_len = 0
    for char in s:
        if char == '(':
            open_count += 1
        else:
            close_count += 1
        if close_count == open_count:
            max_len = max(max_len, 2 * close_count)
        elif close_count > open_count:
            open_count = close_count = 0
    open_count = close_count = 0
    for i in range(len(s) - 1, -1, -1):
        if s[i] == '(':
            open_count += 1
        else:
            close_count += 1
        if close_count == open_count:
            max_len = max(max_len, 2 * close_count)
        elif open_count > close_count:
            open_count = close_count = 0
    return max_len

