def max_contest_prettiness(queries):

    def calc(X, Y):
        if len(Y) != 3:
            return sum(Y)
        for x in X:
            for y in Y:
                if y % x == 0:
                    break
            else:
                return calc([i for i in X if i != x], sorted(Y + [x])[::-1])
        return sum(Y)
    results = []
    for query in queries:
        A = sorted(set(query))[::-1]
        results.append(max(calc(A, []), calc(A[1:], [])))
    return results

