def find_smallest_periodic_number(L: int, A: str) -> str:
    ans = A[:L]
    if L != len(A):
        ans = list(ans)
        for i in range(L - 1, -1, -1):
            if ans[i] != '9':
                ans[i] = chr(ord(ans[i]) + 1)
                break
            else:
                ans[i] = '0'
        ans = ''.join(ans)
        return ans
    elif len(A) % L or ans == '9' * L:
        return ('1' + '0' * (L - 1)) * (len(A) // L + 1)
    else:
        ch = 1
        for i in range(L, len(A) - L + 1, L):
            if A[:L] > A[i:i + L]:
                ch = 0
                break
        if ch:
            ans = list(ans)
            for i in range(L - 1, -1, -1):
                if ans[i] != '9':
                    ans[i] = chr(ord(ans[i]) + 1)
                    break
                else:
                    ans[i] = '0'
            ans = ''.join(ans)
        return ans * (len(A) // L)

