def calculate_minimum_diameter(n, s, edges):
    if n != 2:
        return float(s)
    degree = [0] * n
    for (a, b) in edges:
        degree[a - 1] += 1
        degree[b - 1] += 1
    leaf_count = sum((1 for d in degree if d == 1))
    return 2 * (s / leaf_count)

