def is_polygon_b_inside_polygon_a(polygon_a, polygon_b):

    def convex_hull(points):
        points = sorted(set(points))
        if len(points) <= 1:
            return points

        def cross(o, a, b):
            return (a[0] - o[0]) * (b[1] - o[1]) - (a[1] - o[1]) * (b[0] - o[0])
        lower = []
        for p in points:
            while len(lower) >= 2 and cross(lower[-2], lower[-1], p) < 0:
                lower.pop()
            lower.append(p)
        upper = []
        for p in reversed(points):
            while len(upper) >= 2 and cross(upper[-2], upper[-1], p) < 0:
                upper.pop()
            upper.append(p)
        return lower[:-1] + upper[:-1]
    total = polygon_a - polygon_b
    set_a = set(polygon_a)
    for vertex_b in polygon_b:
        if vertex_b in set_a:
            return 'NO'
    if sorted(polygon_a) == sorted(convex_hull(total)):
        return 'YES'
    else:
        return 'NO'

