from collections import deque
from itertools import accumulate

def arr_sum(arr):
    arr.appendleft(1)
    return list(accumulate(arr, lambda x, y: x + y))

def minimum_lost_milk(n, a):
    a = deque(a)
    mem = arr_sum(a.copy())
    (ans1, ans0) = (0, 0)
    for i in range(n):
        if a[i]:
            ans1 += n - (i + 1) - (mem[-1] - mem[i + 1])
        else:
            ans0 += mem[i]
    return min(ans0, ans1)

