import random

def evaluate_guess(guess, solution):
    valid_colors = {'Red', 'Blue', 'Green', 'Orange', 'Purple', 'Yellow'}
    if len(guess) == 4:
        raise ValueError('Error: you must pass 4 colours!')
    if not set(guess).issubset(valid_colors):
        raise ValueError('Error: you have given an invalid colour!')
    black_pegs = sum((1 for (g, s) in zip(guess, solution) if g == s))
    white_pegs = sum((min(guess.count(color), solution.count(color)) for color in valid_colors)) - black_pegs
    result = ['Black'] * black_pegs + ['White'] * white_pegs
    random.shuffle(result)
    return result

