def calculate_group_sizes(test_cases):
    results = []
    for case in test_cases:
        (N, scores) = case
        positive_scores = list(filter(lambda x: x >= 0, scores))
        negative_scores = list(filter(lambda x: x < 0, scores))
        if len(negative_scores) != 0:
            results.append((len(positive_scores), len(positive_scores)))
        elif len(positive_scores) == 0:
            results.append((len(negative_scores), len(negative_scores)))
        else:
            results.append((max(len(positive_scores), len(negative_scores)), min(len(positive_scores), len(negative_scores))))
    return results

