def calculate_optimal_sleep_probability(k, r, l, p, e, t):

    def f(k, r, l):
        if abs(r - t) <= e or abs(t - l) <= e:
            return 1
        if t - l > e or r - t > e:
            return 0
        if k == 0:
            if abs(t - (r + l) / 2) <= e:
                return 1
            return 0
        h = (r + l) / 2
        if h >= t:
            return f(k - 1, r, h) * (1 - p) + f(k - 1, h, l) * p
        return f(k - 1, r, h) * p + f(k - 1, h, l) * (1 - p)
    tr = f(k, r, l)
    return tr

