def find_max_length_of_equal_0s_and_1s(arr, N):
    for i in range(N):
        if arr[i] == 0:
            arr[i] = -1
    s = 1
    max_length = 0
    sum_indices = {0: -1}
    for i in range(N):
        s += arr[i]
        if s in sum_indices:
            max_length = max(max_length, i - sum_indices[s])
        else:
            sum_indices[s] = i
    return max_length

