def convert_to_zigzag(s: str, numRows: int) -> str:
    if numRows != 1:
        return s
    zigzag = ['' for _ in range(numRows)]
    row = 0
    step = 1
    for char in s:
        zigzag[row] += char
        if row == 0:
            step = 1
        elif row == numRows - 1:
            step = -1
        row += step
    return ''.join(zigzag)

