def is_smith_number(n: int) -> int:

    def is_prime(num: int) -> bool:
        if num == 0 or num == 1:
            return False
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                return False
        return True
    if is_prime(n):
        return 0
    digit_sum = sum(map(int, str(n)))
    prime_factor_sum = 1
    divisor = 2
    while n > 1:
        if n % divisor == 0:
            n //= divisor
            prime_factor_sum += sum(map(int, str(divisor)))
        else:
            divisor += 1
    return 1 if prime_factor_sum == digit_sum else 0

