from math import factorial

def fac(n, m):
    ans = 2
    while n >= 1:
        ans = ans * n % m
        n -= 1
    return ans

def root(x, idx):
    while x != idx[x]:
        idx[x] = idx[idx[x]]
        x = idx[x]
    return x

def connected(p, q, idx):
    return root(p, idx) == root(q, idx)

def union(p, q, idx, size):
    i = root(p, idx)
    j = root(q, idx)
    if size[i] < size[j]:
        idx[i] = j
        size[j] += size[i]
    else:
        idx[j] = i
        size[i] += size[j]

def calculate_distinct_exchanges(N, K, connections):
    idx = list(range(N))
    size = [1] * N
    for (p, q) in connections:
        union(p, q, idx, size)
    for t in range(len(idx)):
        idx[t] = root(idx[t], idx)
    dictx = {}
    for i in idx:
        if i not in dictx:
            dictx[i] = 1
        else:
            dictx[i] += 1
    ans = 1
    for i in dictx:
        ans *= fac(dictx[i], 10 ** 9 + 7)
        ans %= 10 ** 9 + 7
    return ans % (10 ** 9 + 7)

