def minimize_error(n, k1, k2, A, B):
    d = [abs(A[i] - B[i]) for i in range(n)]
    k = k1 - k2
    for _ in range(k):
        mx = max(d)
        id = d.index(mx)
        if mx == 0:
            d[id] += 1
        else:
            d[id] -= 1
    return sum([x ** 2 for x in d])

