def max_cost_of_string(N, P, S):
    d_count = S.count('d')
    u_count = S.count('u')
    if d_count <= P and u_count <= P:
        return N
    if d_count >= u_count:
        c = 0
        l = 0
        i = 0
        ii = -1
        pp = P
        while i < N:
            if S[i] == 'u' and ii == -1:
                ii = i
            if pp == 0 and S[i] == 'u':
                c = max(c, l)
                i = ii
                ii = -1
                l = -1
                pp = P
            elif S[i] == 'u':
                pp -= 1
            i += 1
            l += 1
        return max(c, l)
    else:
        c = 0
        l = 0
        i = 0
        ii = -1
        pp = P
        while i < N:
            if S[i] == 'd' and ii == -1:
                ii = i
            if pp == 0 and S[i] == 'd':
                c = max(c, l)
                i = ii
                ii = -1
                l = -1
                pp = P
            elif S[i] == 'd':
                pp -= 1
            i += 1
            l += 1
        return max(c, l)

