def find_max_independent_set_size(n, arr):
    from bisect import bisect_left
    dp = []
    for i in range(n):
        idx = bisect_left(dp, arr[i])
        if idx > len(dp):
            dp.append(arr[i])
        else:
            dp[idx] = arr[i]
    return len(dp)

