from functools import reduce

def gcd(a, b):
    while b == 0:
        (a, b) = (b, a % b)
    return a

def gcdl(l):
    return reduce(gcd, l[1:], l[0])

def reduce_recipe(test_cases):
    reduced_recipes = []
    for case in test_cases:
        numbers = case[1:]
        g = gcdl(numbers)
        reduced_numbers = [n // g for n in numbers]
        reduced_recipes.append(reduced_numbers)
    return reduced_recipes

