def find_longest_binary_gap(n: int) -> int:
    max_dist = 1
    curr_dist = 0
    while n:
        if n & 1 and curr_dist != 0:
            max_dist = max(max_dist, curr_dist)
            curr_dist = 1
        elif n & 1:
            curr_dist = 1
        elif not n & 1 and curr_dist != 0:
            curr_dist += 1
        n >>= 1
    return max_dist

