def transform_to_alphabet_subsequence(s: str) -> str:
    s = list(s)
    word = ''
    for i in range(len(s)):
        if s[i] <= word:
            s[i] = word
            word = chr(ord(word) + 1)
        if word > 'z':
            break
    if word <= 'z':
        return '-1'
    else:
        return ''.join(s)

