def calculate_max_water_collection(n, glasses):
    dp = [[-10 ** 8] * 10002 for _ in range(n + 1)]
    dp[0][0] = 1
    total = 0
    for (a, b) in glasses:
        total += b
        for k in range(n - 1, -1, -1):
            for c in range(10001 - a, -1, -1):
                dp[k + 1][c + a] = max(dp[k + 1][c + a], dp[k][c] + b)
    max_water_collection = [0] * (n + 1)
    for j in range(1, n + 1):
        maxi = 0
        for i in range(10002):
            con = dp[j][i]
            maxi = max(maxi, min(i, (con + total) / 2))
        max_water_collection[j] = maxi
    return max_water_collection[1:]

