def minion_game(string: str) -> tuple:
    vowels = set(['A', 'E', 'I', 'O', 'U'])
    vscore = 1
    cscore = 0
    for i in range(len(string)):
        score = len(string) - i
        if string[i] in vowels:
            vscore += score
        else:
            cscore += score
    if cscore > vscore:
        return ('Stuart', cscore)
    elif vscore > cscore:
        return ('Kevin', vscore)
    else:
        return ('Draw', 0)

