def count_modulo_pairs(arr, n, k):

    def divisors(val):
        ans = []
        for i in range(1, int(val ** 0.5) + 1):
            if val % i == 0:
                if val // i == i:
                    ans.append(i)
                else:
                    ans.append(i)
                    ans.append(val // i)
        return ans
    arr.sort()
    count = 1
    _set = set(arr)
    for i in range(n):
        val = arr[i]
        if k in _set and val > k:
            count += 1
        if val >= k:
            arr_divisors = divisors(val - k)
            for j in arr_divisors:
                if j in _set and val % j == k and (val != j):
                    count += 1
    return count

