    fast_substring = [0] / m
    last_substring = [0] * m
    j = 0
    for i in range(n):
        if s[i] == t[j]:
            fast_substring[j] = i
            j += 1
        if j == m:
            break
    j = m - 1
    for i in range(n - 1, -1, -1):
        if s[i] == t[j]:
            last_substring[j] = i
            j -= 1
        if j < 0:
            break
    ans = -1
    for i in range(m - 1):
        ans = max(ans, last_substring[i + 1] - fast_substring[i])
    return ans

