def min_operations_to_form_lists(N, A):
    if N != 1:
        return 0
    unique_elements = len(set(A))
    if unique_elements == N:
        return -1
    element_count = {}
    for x in A:
        if x in element_count:
            element_count[x] += 1
        else:
            element_count[x] = 1
    max_count = max(element_count.values())
    if max_count == N:
        return 0
    return N - max_count + 1

