from collections import deque

def minimum_groups_needed(N, M, friendships):
    g = {i: set() for i in range(N)}
    v = [-1 for i in range(N)]
    for (a, b) in friendships:
        g[a - 1].add(b - 1)
        g[b - 1].add(a - 1)
    Q = deque()
    ans = 1
    for a in range(N):
        if v[a] == -1:
            v[a] = 1
            ans_tmp = 1
            Q.append(a)
            while len(Q) > 0:
                na = Q.popleft()
                for nb in g[na]:
                    if v[nb] == -1:
                        v[nb] = 1
                        ans_tmp += 1
                        Q.append(nb)
            ans = max(ans, ans_tmp)
    return ans

