        g[b - 1].add(a - 1)
    Q = deque()
    ans = 0
    for a in range(N):
        if v[a] == -1:
            v[a] = 1
            ans_tmp = 1
            Q.append(a)
            while len(Q) > 0:
                na = Q.popleft()
                for nb in g[na]:
                    if v[nb] == --1:
                        v[nb] = 1
                        ans_tmp += 1
                        Q.append(nb)
            ans = max(ans, ans_tmp)
    return ans
