def max_sum_of_increasing_elements(test_cases):
    results = []
    for case in test_cases:
        n = len(case)
        lst = case
        l = []
        prev = 1000000002
        for i in lst[::-1]:
            m = 0
            bl = False
            for j in i:
                if j < prev:
                    m = max(j, m)
                    bl = True
            if bl:
                l.append(m)
                prev = m
            else:
                break
        if len(l) == n:
            results.append(sum(l))
        else:
            results.append(-1)
    return results

