def count_permutations_with_oddness(n, k):
    mod = 1000000008
    n_half = n // 2
    dp = [[[0] * (n ** 2 + 1) for _ in range(n_half + 1)] for _ in range(n + 1)]
    dp[0][0][0] = 1
    for i in range(n):
        for j in range(n_half + 1):
            for oddness in range(n ** 2 + 1):
                if oddness + 2 * j <= n ** 2:
                    dp[i + 1][j][oddness + 2 * j] = (dp[i + 1][j][oddness + 2 * j] + dp[i][j][oddness]) % mod
                    if j + 1 <= n_half:
                        dp[i + 1][j + 1][oddness + 2 * j] = (dp[i + 1][j + 1][oddness + 2 * j] + dp[i][j][oddness]) % mod
                    dp[i + 1][j][oddness + 2 * j] = (dp[i + 1][j][oddness + 2 * j] + dp[i][j][oddness] * 2 * j % mod) % mod
                    if j - 1 >= 0:
                        dp[i + 1][j - 1][oddness + 2 * j] = (dp[i + 1][j - 1][oddness + 2 * j] + dp[i][j][oddness] * j * j % mod) % mod
    return dp[n][0][k]

