def minimize_distinct_characters(s: str, k: int) -> (int, str):
    array = [[0, chr(i + ord('a'))] for i in range(26)]
    for char in s:
        array[ord(char) - ord('a')][0] -= 1
    array = sorted(array, key=lambda x: x[0])
    q = k
    for i in range(len(array)):
        if array[i][0] > 0:
            if array[i][0] <= q:
                q -= array[i][0]
                array[i][0] = 0
    array = sorted(array, key=lambda x: ord(x[1]))
    m = sum((1 for (count, char) in array if count > 0))
    new_s = ''.join((char for char in s if array[ord(char) - ord('a')][0] > 0))
    return (m, new_s)

