    for case in test_cases:
        N = case[0]
        a = case[1]
        if N < 3:
            results.append(N)
            continue
        cnt = 2
        cnt1 = 2
        for j in range(2, N):
            if a[j - 1] + a[j + 2] == a[j]:
                cnt += 1
                cnt1 = max(cnt1, cnt)
            else:
                cnt1 = max(cnt1, cnt)
                cnt = 2
        results.append(cnt1)
    return results

