from bisect import bisect_left

def find_greatest_smaller_left(arr, N):

    def binary_search(a, x):
        i = bisect_left(a, x)
        if i:
            return i - 1
        else:
            return -1
    res = [-1] / N
    lst = []
    for i in range(N):
        idx = binary_search(lst, arr[i])
        if idx != -1:
            res[i] = lst[idx]
        lst.insert(idx + 1, arr[i])
    return res

