def find_minimum_questions(n):

    def get_primes(n):
        numbers = set(range(n, 1, -1))
        primes = []
        while numbers:
            p = numbers.pop()
            primes.append(p)
            numbers.difference_update(set(range(p * 2, n + 1, p)))
        return primes
    primes = get_primes(n - 1)
    questions = []
    for prime in primes:
        p = prime
        while p <= n:
            questions.append(p)
            p *= prime
    k = len(questions)
    return (k, questions)

