def convert_string(s: str) -> str:
    """"""
    ans = ''
    for char in s:
        if 'a' <= char <= 'z':
            ans += chr(ord('z') - (ord(char) - ord('a')))
        elif 'A' <= char <= 'Z':
            ans += chr(ord('Z') - (ord(char) - ord('A')))
        else:
            ans += char
    return ans
