def find_lexicographically_smallest_sequence(T, test_cases):
    results = []
    for case in test_cases:
        (N, X, arr) = case
        rem = 1
        for i in range(N - 1):
            a = arr[i]
            d = a & rem
            (a, rem) = (a ^ d, rem ^ d)
            b = bin(a).count('1')
            if b <= X:
                X -= b
                rem ^= a
                a = 0
            elif X > 0:
                d = 0
                for j in range(31, -1, -1):
                    if a & 1 << j != 0:
                        d ^= 1 << j
                        X -= 1
                        if X == 0:
                            break
                (a, rem) = (a ^ d, rem ^ d)
            arr[i] = a
        arr[-1] ^= rem
        if X == 1 or (N == 2 and X % 2 == 1):
            arr[-2] = 1
            arr[-1] ^= 1
        results.append(arr)
    return results

